mapper.select:
select a.id, a.author_id, a.author_name, a.category_id, a.category_name, a.title, a.cover_image, a.intro, a.content, a.read_type, a.is_top, a.status, a.is_forward, a.forward_url, ifnull(a.likes,0) as likes, ifnull(a.collection,0) as collection, ifnull(a.comment,0) as comment, ifnull(a.views,0) as views, a.create_time, a.update_time, ac.name as categoryName,
group_concat( at.name SEPARATOR ',' ) AS tags from t_article a left join t_article_category ac on a.category_id = ac.id left join t_article_tag at ON FIND_IN_SET(at.id, a.tags)
a.status = #{action.status}
and a.is_forward = #{action.isForward}
and a.is_top = #{action.isTop}
and a.category_id = #{action.categoryId}
and a.title like concat('%', #{action.title}, '%')
and a.create_time >= #{action.createTimeStart}
and #{action.createTimeEnd} >= a.create_time
and FIND_IN_SET(#{action.tags}, a.tags)
GROUP BY a.id ORDER BY a.id DESC

update.foreach:
update t_article


        views = views + #{item.views},


        likes = likes + #{item.likes}


where id = #{item.id}
